import React, { Component } from "react";
import { Form, Grid, Image, Transition } from "semantic-ui-react";
import { reduxForm, Field } from "redux-form";
import { Link } from "react-router-dom";

export default class TransitionExampleSingleExplorer extends Component {
  state = { animation: "fly up", duration: 1100, visible: true };

  handleVisibility = e => {
    e.preventDefault();
    this.setState({ visible: !this.state.visible });
  };

  render() {
    const { animation, duration, visible } = this.state;

    return (
      <div>
        <button onClick={this.handleVisibility}>Button</button>
        <Transition.Group animation={animation} duration={duration}>
          {visible && (
            <form onSubmit={this.onSubmit} className="ui form">
              <fieldset className="field">
                <label>Title </label>
                <Field
                  name="title"
                  type="text"
                  component="input"
                  autoComplete="none"
                  className="ui input"
                />
              </fieldset>

              <fieldset className="field">
                <label>Link </label>
                <Field
                  name="link"
                  type="text"
                  component="input"
                  autoComplete="none"
                  className="ui input"
                />
              </fieldset>
              <fieldset className="field">
                <label>Email </label>
                <Field
                  name="email"
                  type="text"
                  component="textarea"
                  autoComplete="none"
                />
              </fieldset>
              <fieldset className="field">
                <Field name="genre" component="select">
                  <option />
                  <option value="nihongo">nihongo</option>
                  <option value="series">series</option>
                  <option value="anime">anime</option>
                  <option value="programing">programing</option>
                  <option value="other">other</option>
                </Field>
              </fieldset>
              <button className="ui primary button">Submit</button>
            </form>
          )}
        </Transition.Group>
      </div>
    );
  }
}
