import React, { Component } from "react";
import { Form, Grid, Image, Transition } from "semantic-ui-react";

const options = {
  key: "fly up",
  text: "fly up",
  value: "fly up"
};

export default class TransitionExampleSingleExplorer extends Component {
  state = { animation: "fly up", duration: 500, visible: true };

  handleVisibility = e => {
    e.preventDefault();
    this.setState({ visible: !this.state.visible });
  };

  render() {
    const { animation, duration, visible } = this.state;

    return (
      <Grid columns={2}>
        <button onClick={this.handleVisibility}>Button</button>

        <Grid.Column>
          <Transition.Group animation="fly up" duration={500}>
            {visible && (
              <Image centered size="small" src="/images/leaves/4.png" />
            )}
          </Transition.Group>
        </Grid.Column>
      </Grid>
    );
  }
}
