import {
  AUTH_USER,
  AUTH_ERROR,
  SIGN_IN,
  SIGN_OUT,
  ADD_LINK
} from "../actions/types";

const INITIAL_STATE = {
  authenticated: "",
  errorMessage: "",
  isSignedIn: false,
  userId: null
};

export default function(state = INITIAL_STATE, action) {
  switch (action.type) {
    case AUTH_USER:
      return { ...state, authenticated: action.payload };
    case AUTH_ERROR:
      return { ...state, errorMessage: action.payload };
    case SIGN_IN:
      return { ...state, userId: action.payload, isSignIn: true };
    case SIGN_OUT:
      return {
        ...state,
        authenticated: "",
        isSignIn: false,
        userId: null
      };
    case ADD_LINK:
      return state;
    default:
      return state;
  }
}
