import React from "react";
import axios from "axios";

import weather from "./src/weather.png";

class Weather extends React.Component {
  state = { latitude: 45.376296, longitude: -75.651582, el: null };
  async forecastWeather(latitude, longitude) {
    console.log(latitude, longitude);
    try {
      const response = await axios.post(
        `http://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=219a5f4286f667b3eeb09e9a5efa7f85`
      );
      this.setState({ el: <div>{response.data.weather[0].main}</div> });
      console.log(response.data.name);
      console.log(response.data);
      console.log(response.data.main.temp);
      console.log(response.data.main.pressure);
      console.log(response.data.main.humidity);
      console.log(response.data.wind.speed);
      console.log(response.data.wind.deg);
      console.log(response.data.weather[0].main);
    } catch (error) {
      console.log(error);
    }
  }
  render() {
    return (
      <img
        onClick={() =>
          this.forecastWeather(this.state.latitude, this.state.longitude)
        }
        className="api_icon"
        src={weather}
        alt="weather_icon"
      />
    );
  }
}

export default Weather;
