import React from "react";
import axios from "axios";

import weather from "./src/newspaper.png";

class Weather extends React.Component {
  state = { latitude: 45.376296, longitude: -75.651582, el: null };
  async searchNews(country) {
    try {
      const response = await axios.post(
        `https://newsapi.org/v2/top-headlines?country=${country}&apiKey=883726e427a541609ad6a7e2dc25b971`
      );
      console.log(response);
    } catch (error) {
      console.log(error);
    }
  }
  render() {
    return (
      <img
        src={newspaper}
        onClick={() => this.searchNews("canada")}
        className="api_icon news"
        alt="icon_newspaper"
      />
    );
  }
}

export default Weather;
