import React from "react";
import { Link } from "react-router-dom";

class Links extends React.Component {
  state = { showForm: true };
  handleClink = () => {
    console.log(this.state);
    if (this.state) {
      this.setState({ showForm: false });
    } else {
      this.setState({ showForm: true });
    }
  };
  renderForm() {
    return (
      <form onSubmit={handleSubmit(this.onSubmit)}>
        <fieldset>
          <label>Email </label>
          <Field
            name="email"
            type="text"
            component="input"
            autoComplete="none"
          />
        </fieldset>
        <fieldset>
          <label>password </label>
          <Field
            name="password"
            type="password"
            component="input"
            autoComplete="none"
          />
        </fieldset>
        <div>{this.props.errorMessage}</div>
        <button>Sign Up!</button>
      </form>
    );
  }
  render() {
    return (
      <div>
        {renderForm()}
        <button onClick={this.handleClink}>Hide Form</button>
      </div>
    );
  }
}

export default Links;
