import React from "react";
import axios from "axios";

import weather from "./src/weather.png";
import Modal from "components/Modal";

class Weather extends React.Component {
  state = { latitude: 45.376296, longitude: -75.651582, el: null };
  renderModal() {
    console.log(this.state.el);

    if (this.state.el !== null) {
      return (
        <Modal
          title=""
          content={this.state.el}
          actions=""
          onDismiss={() => history.push("/global")}
        />
      );
    }
    return null;
  }
  async forecastWeather(latitude, longitude) {
    console.log(latitude, longitude);
    try {
      const response = await axios.get(
        `http://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=219a5f4286f667b3eeb09e9a5efa7f85`
      );
      this.setState({ el: <div>{response.data.weather[0].main}</div> });
      this.renderContent = (
        <React.Fragment>
          <p>{response.data.name}</p>
          <p>{response.data}</p>
          <p>{response.data.main.temp}</p>
          <p>{response.data.main.pressure}</p>
          <p>{response.data.main.humidity}</p>
          <p>{response.data.wind.speed}</p>
          <p>{response.data.wind.deg}</p>
          <p>{response.data.weather[0].main}</p>
        </React.Fragment>
      );
    } catch (error) {
      console.log(error);
    }
  }
  render() {
    return (
      <div>
        <img
          onClick={() =>
            this.forecastWeather(this.state.latitude, this.state.longitude)
          }
          className="api_icon"
          src={weather}
          alt="weather_icon"
        />
        {this.renderModal()}
      </div>
    );
  }
}

export default Weather;
