import React from "react";
import { connect } from "react-redux";
import * as actions from "actions";

class Content extends React.Component {
  state = {
    hiddeRelax: "",
    hiddeExciting: "",
    hiddePositive: "",
    hiddeNegative: "",
    hiddeOld_but_gold: ""
  };

  componentDidMount() {
    this.props.getSongs(() => {
      console.log("received");
    });
  }
  toggleClass(css) {
    console.log(css);

    if (css !== "") {
      this.setState({ css: "hiden" });
    }
  }
  renderContent() {
    if (this.props.songs) {
      return this.props.songs.songs.map((song, i) => {
        const relax = song.relax === true ? "relax " : this.state.hiddeRelax;
        const exciting =
          song.exciting === true ? "exciting " : this.state.hiddeExciting;
        const positive =
          song.positive === true ? "positive " : this.state.hiddePositive;
        const negative =
          song.negative === true ? "negative " : this.state.hiddeNegative;
        const old_but_gold =
          song.old_but_gold === true
            ? "old_but_gold "
            : this.state.hiddeOld_but_gold;
        const tags = relax + exciting + positive + negative + old_but_gold;
        return (
          <div key={i} className={`ui inverted red button ${tags}`}>
            {song.music}
          </div>
        );
      });
    }
    return <div>Loading...</div>;
  }
  render() {
    return (
      <div>
        <div className="sideBar">
          <button
            className="ui inverted blue button"
            onClick={() => this.toggleClass(this.state.hiddeRelax)}
          >
            relax
          </button>
          <button className="ui inverted blue button">exciting</button>
          <button className="ui inverted blue button">positive</button>
          <button className="ui inverted blue button">negative</button>
          <button className="ui inverted blue button">old but gold</button>
        </div>
        {this.renderContent()}
      </div>
    );
  }
}
const mapStatetoProps = state => {
  return {
    songs: state.song.songs
  };
};
export default connect(
  mapStatetoProps,
  actions
)(Content);
