import React from "react";
import { reduxForm, Field } from "redux-form";
import { Link } from "react-router-dom";
import "./css/linksStyle.css";
import TransitionExampleSingleExplorer from "./TransitionExampleSingleExplorer";
class Links extends React.Component {
  state = { showForm: true };

  handleClink = () => {
    if (this.state) {
      this.setState({ showForm: false });
    } else {
      this.setState({ showForm: true });
    }
  };
  onSubmit() {
    console.log("submitted");
  }
  renderForm() {
    return (
      <form onSubmit={this.onSubmit} className="ui form">
        <fieldset className="field">
          <label>Title </label>
          <Field
            name="title"
            type="text"
            component="input"
            autoComplete="none"
            className="ui input"
          />
        </fieldset>

        <fieldset className="field">
          <label>Link </label>
          <Field
            name="link"
            type="text"
            component="input"
            autoComplete="none"
            className="ui input"
          />
        </fieldset>
        <fieldset className="field">
          <label>Email </label>
          <Field
            name="email"
            type="text"
            component="textarea"
            autoComplete="none"
          />
        </fieldset>
        <fieldset className="field">
          <Field name="genre" component="select">
            <option />
            <option value="nihongo">nihongo</option>
            <option value="series">series</option>
            <option value="anime">anime</option>
            <option value="programing">programing</option>
            <option value="other">other</option>
          </Field>
        </fieldset>
        <button className="ui primary button">Submit</button>
      </form>
    );
  }
  render() {
    return (
      <div>
        <button onClick={this.handleClink}>Hide Form</button>
        <button onClick={console.log(this)}>Hide Form</button>
        <LinksForm />
      </div>
    );
  }
}

export default reduxForm(null)(Links);
