import React, { Component } from "react";
import { Transition } from "semantic-ui-react";
import { reduxForm, Field } from "redux-form";
import { compose } from "redux";
import { connect } from "react-redux";
import * as actions from "actions";

import "components/links/linksFormStyle.css";
class LinksForm extends Component {
  state = { visible: true, errorMessage: "" };
  componentDidUpdate() {
    console.log(this);
  }
  handleVisibility = e => {
    e.preventDefault();
    this.setState({ visible: !this.state.visible });
  };
  onSubmit = formProps => {
    this.props.addLink(formProps, () => {
      /* return <div>{this.props.genericResponse}</div>; */
      if (
        this.props.genericResponse === "Success" ||
        this.state.visible === true
      ) {
        this.setState({ visible: false });
      } else if (this.props.genericResponse !== "") {
        this.setState({ errorMessage: this.props.genericResponse });
      }
    });
  };

  render() {
    const animation = "fly up";
    const visible = this.state.visible;
    const duration = 1100;
    const { handleSubmit } = this.props;

    return (
      <div id="linksForm">
        <button
          className="ui primary button puller"
          onClick={this.handleVisibility}
        >
          Add Link
        </button>
        <Transition.Group animation={animation} duration={duration}>
          {visible && (
            <form onSubmit={handleSubmit(this.onSubmit)} className="ui form">
              {this.state.errorMessage}
              <fieldset className="field">
                <label>Title </label>
                <Field
                  name="title"
                  type="text"
                  component="input"
                  autoComplete="none"
                  className="ui input"
                />
              </fieldset>

              <fieldset className="field">
                <label>Link </label>
                <Field
                  name="link"
                  type="text"
                  component="input"
                  autoComplete="none"
                  className="ui input"
                />
              </fieldset>
              <fieldset className="field">
                <label>Description </label>
                <Field
                  name="description"
                  type="text"
                  component="textarea"
                  autoComplete="none"
                />
              </fieldset>
              <fieldset className="field">
                <Field name="genre" component="select">
                  <option />
                  <option value="nihongo">nihongo</option>
                  <option value="series">series</option>
                  <option value="anime">anime</option>
                  <option value="programing">programing</option>
                  <option value="other">other</option>
                </Field>
              </fieldset>
              <button className="ui primary button centered">Submit</button>
            </form>
          )}
        </Transition.Group>
      </div>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  if (state.auth.genericResponse) {
    return { genericResponse: state.auth.genericResponse };
  }
  return { submit: null };
};
export default compose(
  connect(
    mapStateToProps,
    actions
  ),
  reduxForm({ form: "addLinkForm" })
)(LinksForm);
