import React from "react";
import { reduxForm, Field } from "redux-form";
import { Link } from "react-router-dom";

class Links extends React.Component {
  state = { showForm: true };
  handleClink = () => {
    console.log(this.state);
    if (this.state) {
      this.setState({ showForm: false });
    } else {
      this.setState({ showForm: true });
    }
  };
  handleSubmit() {
    console.log("submitted");
  }
  renderForm() {
    return (
      <form onSubmit={this.handleSubmit(this.onSubmit)}>
        <fieldset>
          <label>Title </label>
          <Field
            name="title"
            type="text"
            component="input"
            autoComplete="none"
          />
        </fieldset>

        <fieldset>
          <label>Link </label>
          <Field
            name="link"
            type="text"
            component="input"
            autoComplete="none"
          />
        </fieldset>
        <fieldset>
          <label>Email </label>
          <Field
            name="email"
            type="text"
            component="input"
            autoComplete="none"
          />
        </fieldset>
        <fieldset>
          <Field name="genre" component="select">
            <option />
            <option value="nihongo">nihongo</option>
            <option value="series">series</option>
            <option value="anime">anime</option>
            <option value="programing">programing</option>
            <option value="other">other</option>
          </Field>
        </fieldset>
        <div>{this.props.errorMessage}</div>
        <button>Sign Up!</button>
      </form>
    );
  }
  render() {
    return (
      <div>
        {this.renderForm()}
        <button onClick={this.handleClink}>Hide Form</button>
      </div>
    );
  }
}

export default reduxForm()(Links);
