import { AUTH_USER, AUTH_ERROR, SIGN_IN, SIGN_OUT } from "../actions/types";

const INITIAL_STATE = {
  authenticated: "1",
  errorMessage: "",
  isSignedIn: false,
  userId: 1
};

export default function(state = INITIAL_STATE, action) {
  switch (action.type) {
    case AUTH_USER:
      return { ...state, authenticated: action.payload };
    case AUTH_ERROR:
      return { ...state, errorMessage: action.payload };
    case SIGN_IN:
      return { ...state, isSignedIn: true, userId: action.payload };
    case SIGN_OUT:
      return { ...state, isSignedIn: false, userId: null };
    default:
      return state;
  }
}
