import React from "react";
import { Flag, Segment } from "semantic-ui-react";
import axios from "axios";

import newspaper from "./src/newspaper.png";
import weather from "./src/weather.png";
import twitter from "./src/twitter.png";
import youtube from "./src/youtube.png";

/* import "./globalStyle.css"; */

class Cell extends React.Component {
  state = { el: null };
  componentDidMount() {}
  async forecastWeather(latitude, longitude) {
    console.log(latitude, longitude);
    try {
      const response = await axios.post(
        `http://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=219a5f4286f667b3eeb09e9a5efa7f85`
      );
      this.setState({ el: <div>{response.data.weather[0].main}</div> });
      console.log(response.data.weather[0].main);
      console.log(response);
    } catch (error) {
      console.log(error);
    }
    /* xhttp.onreadystatechange = function() {
      if (this.readyState == 4 && this.status == 200) {
        var response = JSON.parse(this.responseText);
        console.log(response);
        console.log(typeof response);
        var weatherType = response.weather[0].description;
        var kelvin = response.main.temp;
        var windSpeed = response.wind.speed;
        var city = response.name;
        var country = response.sys.country;
        console.log(weatherType);
        console.log(kelvin);
      }
    }; */
  }
  render() {
    return (
      <Segment>
        {this.state.el}
        <Flag name={this.props.code} />
        <div id={`fuso_${this.props.code}`} />
        <h5>{this.props.name}</h5>
        <div className="row">
          <img
            id="canada_news"
            src={newspaper}
            className="api_icon news"
            alt="icon_newspaper"
          />
          <img
            id="weather_ca"
            onClick={() => this.forecastWeather(45.376296, -75.651582)}
            className="api_icon"
            src={weather}
            alt="weather_icon"
          />
          <img
            id="twitter_ca"
            /* onClick="twitter(45.376296, -75.651582)" */
            className="api_icon"
            src={twitter}
            alt="icon_twitter"
          />
          <img
            id="youtube_ca"
            /* onClick="youtube('ca')" */
            className="api_icon"
            src={youtube}
            alt="icon_youtube"
          />
        </div>
      </Segment>
    );
  }
}

export default Cell;
