import axios from "axios";
import { AUTH_USER, AUTH_ERROR, SIGN_IN, SIGN_OUT, ADD_LINK } from "./types";

export const signup = (formProps, callback) => async dispatch => {
  try {
    const response = await axios.post(
      "http://localhost:3090/signup",
      formProps
    );
    dispatch({ type: AUTH_USER, payload: response.data.token });
    localStorage.setItem("token", response.data.token);
    callback();
  } catch (error) {
    dispatch({ type: AUTH_ERROR, payload: "Email in use" });
  }
};
export const signin = (formProps, callback) => async dispatch => {
  try {
    const response = await axios.post(
      "http://localhost:3090/signin",
      formProps
    );
    dispatch({ type: AUTH_USER, payload: response.data.token });
    localStorage.setItem("token", response.data.token);
    callback();
  } catch (error) {
    dispatch({ type: AUTH_ERROR, payload: "Invalid login credentials" });
  }
};

export const googleSignIn = userId => {
  return {
    type: SIGN_IN,
    payload: userId
  };
};
export const signOut = () => {
  localStorage.removeItem("token");
  return {
    type: SIGN_OUT
  };
};

export const addLink = (formProps, callback) => async dispatch => {
  console.log(formProps);

  try {
    const response = await axios.post(
      "http://localhost:3090/addlink",
      formProps
    );
    dispatch({ type: ADD_LINK, payload: "Sended with success" });
    callback();
  } catch (error) {
    dispatch({ type: ADD_LINK, payload: "Couldn't be sended" });
  }
};
