import React from "react";
import axios from "axios";

import weather from "./src/weather.png";
import Popup from "components/Popup";

class Weather extends React.Component {
  state = {
    latitude: 45.376296,
    longitude: -75.651582,
    name: "",
    temp: "",
    pressure: "",
    humidity: "",
    speed: "",
    deg: "",
    main: ""
  };

  renderPopup() {
    if (this.state.name) {
      return (
        <Popup title={`City: ${this.state.name}`} actions="">
          <h2 className="ui header" />
          <h3>{this.state.main}</h3>
          <p>{this.state.temp - 273.15}ÂºC</p>
          <p>Pressure: {this.state.pressure}kPa</p>
          <p>humidity: {this.state.humidity}%</p>
          <p>wind speed: {this.state.speed}km/h</p>
        </Popup>
      );
    }
    return null;
  }

  async forecastWeather(latitude, longitude) {
    console.log(latitude, longitude);
    try {
      const response = await axios.get(
        `http://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=219a5f4286f667b3eeb09e9a5efa7f85`
      );
      console.log(response);

      this.setState({
        name: response.data.name,
        temp: response.data.main.temp,
        pressure: response.data.main.pressure,
        humidity: response.data.main.humidity,
        speed: response.data.wind.speed,
        deg: response.data.wind.deg,
        main: response.data.weather[0].main
      });
    } catch (error) {
      console.log(error);
    }
  }
  render() {
    return (
      <div>
        <img
          onClick={() =>
            this.forecastWeather(this.state.latitude, this.state.longitude)
          }
          className="api_icon"
          src={weather}
          alt="weather_icon"
        />
        {this.renderPopup()}
      </div>
    );
  }
}

export default Weather;
